new GadgetDescription { Id = Guid.Parse("9bd2f322-212f-4272-813a-6581c28f00cf"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Mình thấy mặt lưng của máy được thiết kế theo dạng vuông vức, tạo nên một vẻ ngoài mạnh mẽ và cá tính. Kiểu dáng này không chỉ làm máy trở nên thanh thoát và sang trọng hơn mà còn mang lại cảm giác thoải mái khi nắm trong lòng bàn tay.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
