new GadgetDescription { Id = Guid.Parse("6b9d722a-1882-4fac-86c4-fa4ecbb970a1"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Mình thấy cảm biến chính 108 MP trên chiếc điện thoại Xiaomi này thực sự là một sự lựa chọn tuyệt vời cho những người đam mê nhiếp ảnh. Độ sắc nét và chi tiết của ảnh dưới mọi điều kiện ánh sáng là điểm độc đáo của nó, mang lại trải nghiệm thú vị và gia tăng cảm hứng sáng tạo.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
