new GadgetDescription { Id = Guid.Parse("5fa9d62e-6fdf-43e1-88e7-186990e9ce25"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Mình rất hài lòng với con chip Snapdragon 685 trên chiếc điện thoại Android này, nó mang đến hiệu năng ổn định và đủ mạnh mẽ để đáp ứng mọi nhu cầu đa dạng của mình. Lướt web, xem video và thậm chí chơi game ở cấu hình trung bình đều diễn ra mượt mà, mình không gặp phải những trở ngại không mong muốn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
