new GadgetDescription { Id = Guid.Parse("5672b0f0-e5a8-44d3-95c6-b84b5b8fdc1f"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Thoải mái sử dụng với pin 5000 mAh", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("f838a442-3e12-4b66-a479-0115bc1ae19e"), GadgetId = Guid.Parse("5723783a-c70a-45d5-aa9a-cbed12e2d18e"), Value = "Viên pin khủng 5000 mAh trên Redmi Note 13 thực sự là nguồn năng lượng mạnh mẽ, vượt trội và có thể cạnh tranh với nhiều đối thủ trên thị trường. Mình đã thử nghiệm và ấn tượng khi chiếc điện thoại này có thể đồng hành cùng mình liên tục trong khoảng 7 tiếng 30 phút, thậm chí khi thực hiện nhiều tác vụ đa nhiệm như chơi game, xem phim và lướt web.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
new GadgetDescription { Id = Guid.Parse("3749f01d-5625-4198-b172-4216712a371e"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Xiaomi 14T được hoàn thiện với sự hợp tác giữa Xiaomi và Leica - thương hiệu máy ảnh danh tiếng từ Đức. Sự kết hợp này hứa hẹn mang đến những bức ảnh sắc nét, giàu tính nghệ thuật, ngay cả trong điều kiện ánh sáng yếu. Máy nổi bật với nhiều tính năng AI thế hệ mới, chip MediaTek Ultra mạnh mẽ, pin bền bỉ, mang lại trải nghiệm toàn diện và mượt mà.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
