new GadgetDescription { Id = Guid.Parse("0b7b7b9f-9ac2-4058-bb2c-9213a98e741c"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Sở hữu màn hình 6.67 inch rộng rãi cùng độ phân giải 1.5K siêu rõ nét, cho khả năng hiển thị cao hơn so với tiêu chuẩn Full HD+, mang lại những khung hình chất lượng với độ chuẩn màu cao, kết hợp cùng với đó là công nghệ phát sáng tùy biến mới, không chỉ cho độ sáng mạnh mẽ mà còn tăng độ tương phản và khả năng hiển thị màu sắc trên thiết bị.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("a674eb9c-f67c-4dad-a97b-90ae1f0b7c4a"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/52a199d0-8dfc-4971-a41d-02fc9d7b79ce.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
