new GadgetDescription { Id = Guid.Parse("454c2781-9267-4815-8f68-60446eb5d2e1"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Xiaomi 14T 5G là một trong những chiếc điện thoại thông minh đáng chú ý nhất trong năm nay, thu hút đông đảo sự quan tâm của người dùng nhờ vào cấu hình mạnh mẽ, đặc biệt là con chip MediaTek Dimensity 8300-Ultra, một vi xử lý được sản xuất trên tiến trình 4 nm tiến tiến, mạnh mẽ cả về hiệu năng xử lý và các tính năng AI tiện lợi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("57a5690d-6c6b-4012-b9fd-6921823af3b1"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/67572333-e90a-490a-8327-b1f382cf2dd6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
