new GadgetDescription { Id = Guid.Parse("f4a98add-ee7b-4de9-b5bb-70c4e419e56b"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Xiaomi 14T 5G trang bị trí tuệ nhân tạo tiên tiến, giúp bạn làm việc hiệu quả hơn. AI trên thiết bị này không chỉ xử lý thông tin nhanh chóng mà còn có khả năng học hỏi thói quen của bạn. Nhờ đó, bạn có thể dễ dàng soạn thảo văn bản, chỉnh sửa ảnh, tìm kiếm thông tin và thực hiện nhiều tác vụ khác chỉ với vài thao tác đơn giản.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("80e1d87b-b291-4552-9398-4ca6243ec76c"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6d431516-1c6e-4709-a04f-d608b3d39ff2.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3867493c-135d-4510-abac-788776e0bf93"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Trình thông dịch AI tiên tiến trong máy, sẽ mang đến trải nghiệm giao tiếp xuyên ngôn ngữ mượt mà cho người dùng. Với khả năng dịch thuật tức thời và chính xác, bạn có thể tự tin tham gia các cuộc gọi điện thoại, cuộc họp trực tuyến và các tương tác trực tiếp với người nước ngoài, xóa bỏ rào cản ngôn ngữ một cách hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("8c811c45-98cb-48dc-b00f-df2a9d83e75a"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Sáng tạo tạo video thú vị và dễ dàng hơn nhờ tính năng phim AI. Người dùng chỉ cần chọn hình ảnh, video và mô tả những ý tưởng bạn muốn, thiết bị sẽ ghi nhận và đưa ra một đoạn video hoàn chỉnh dựa trên yêu cầu và dữ liệu cung cấp của người dùng, làm sống dậy những khoảnh khắc đã cũ và đem đến sự thú vị trong từng thước phim.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("f0c5fdf8-3ddb-4f8c-b5a6-ecff144b4ccf"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Tạo nên những phút giây quay, chụp đầy ấn tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 12 },
new GadgetDescription { Id = Guid.Parse("d39cd56d-a15f-4bc0-8b54-d46db9f54ba9"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Xiaomi 14T 5G sở hữu hệ thống ba camera sau đầy ấn tượng, với camera chính có độ phân giải 50 MP cùng công nghệ chống rung quang học OIS và khẩu độ lớn, cho phép thu nhận lượng ánh sáng nhiều hơn, đặc biệt trong điều kiện thiếu sáng, giúp ghi lại những chi tiết nhỏ nhất của khung cảnh đêm. Bên cạnh camera chính, hai ống kính phụ của máy bao gồm ống kính tele 50 MP và camera góc siêu rộng 12 MP còn mở rộng thêm khả năng zoom quang học và chụp góc rộng, giúp bạn bắt trọn mọi khoảnh khắc đẹp nhất.", Type = ParseGadgetDescriptionType("NormalText"), Index = 13 },
