new GadgetDescription { Id = Guid.Parse("dba5d95b-1ae5-44c7-b3da-f292bb29ab6c"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Viên pin 5000 mAh, sạc nhanh 67W", Type = ParseGadgetDescriptionType("BoldText"), Index = 15 },
new GadgetDescription { Id = Guid.Parse("b6e96a6a-c153-4596-b5ad-e6ecf3781162"), GadgetId = Guid.Parse("86ba9c09-ee3e-422a-b520-272e83ed16c6"), Value = "Viên pin 5000 mAh trên phiên bản này sẽ cung cấp thời gian sử dụng lâu dài và bền bỉ cho người dùng. Theo thông tin từ hãng, thiết bị này cho khả năng sử dụng lên đến 15 giờ cho một lần sạc và sau tối đa 1600 chu kỳ sạc, thì mức pin vẫn duy trì được ở mức 80% dung lượng ban đầu, đảm bảo chiếc điện thoại Xiaomi này sẽ luôn sẵn sàng đồng hành cùng bạn trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 16 },
