new GadgetDescription { Id = Guid.Parse("b17adb4c-2902-4725-9279-132c3e53b570"), GadgetId = Guid.Parse("b83fcff9-0839-4142-9c80-ed70eb7e83ca"), Value = "Bạn đang tìm kiếm một chiếc smartphone giá cả phải chăng nhưng sở hữu camera chất lượng hàng đầu. Xiaomi POCO M6 chính là sự lựa chọn tuyệt vời dành cho bạn. Với camera chính 108 MP sắc nét cùng thiết kế hiện đại và hiệu năng mạnh mẽ, POCO M6 sẽ nâng tầm trải nghiệm di động của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
