new GadgetDescription { Id = Guid.Parse("163189e0-6015-4957-97c9-27b83d09787e"), GadgetId = Guid.Parse("655b676e-86fc-4a84-9d04-15e6e246ba8b"), Value = "Camera đáp ứng tốt nhu cầu cơ bản", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0fe55864-3a64-456e-8ffe-f1f10d73b9f0"), GadgetId = Guid.Parse("655b676e-86fc-4a84-9d04-15e6e246ba8b"), Value = "Với độ phân giải chính 13 MP kết hợp với ống kính phụ 0.08 MP, vivo Y03 mang lại khả năng chụp ảnh tương đối rõ nét. Tính năng xóa phông hay còn được biết đến là chế độ chụp chân dung thông qua camera phụ cho phép người dùng tạo ra những bức ảnh nghệ thuật với việc làm mờ ở phía nền, tập trung tối ưu vào đối tượng chính, tạo nên hiệu ứng thị giác đẹp mắt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3d940a6d-2009-4344-99f6-3cbe7e6d58f4"), GadgetId = Guid.Parse("655b676e-86fc-4a84-9d04-15e6e246ba8b"), Value = "Cấu hình ổn trong tầm giá", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("9cf2a8db-e6df-4415-b24e-fc85a7f0300e"), GadgetId = Guid.Parse("655b676e-86fc-4a84-9d04-15e6e246ba8b"), Value = "Chiếc điện thoại vivo dòng Y này được trang bị hệ điều hành Android phiên bản mới nhất, Android 14, mang lại trải nghiệm người dùng mượt mà, linh hoạt và đầy tính năng. Với Android 14, người dùng có thể trải nghiệm những tính năng mới nhất từ Google và các ứng dụng hàng đầu trên thị trường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("1c5c1716-a673-44ec-bf56-7b4f234ea706"), GadgetId = Guid.Parse("655b676e-86fc-4a84-9d04-15e6e246ba8b"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1247bd3d-f40e-45af-b513-63cef5235143.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("c2f08b90-5e1d-4bf9-b512-869973da4640"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "vivo Y17s mẫu điện thoại được nhà vivo đem đến cho người dùng Việt Nam trong những tháng cuối năm 2023. Vẫn lối thiết kế 2.5D quen thuộc, camera chụp ảnh chất lượng lên đến 50 MP đi kèm với màn hình hiển thị tốt cùng nhiều tính năng khác đang đợi bạn khám phá.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ef84ae67-8fa2-42de-bf21-8c6d833f9b6c"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Màn hình lớn cùng độ sáng cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("e93be81c-6e6e-41ca-a5e4-f8eebb909086"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Về phần màn hình, điện thoại được trang bị tấm nền IPS LCD, một chuẩn khá phổ biến trên các dòng điện thoại giá rẻ đến tầm trung. Với ưu điểm chính có lẽ là giá thành thấp, vì thế chất lượng nó mang lại cũng chỉ nằm ở mức ổn. Màu sắc cho ra chân thực, trung tính và không quá sặc sỡ như AMOLED.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("dbb0a93c-7d03-42af-a988-5003588e6214"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/b7b9c123-de4f-459d-a0bd-ec97085a20d0.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("6a12b1d8-255a-4a3a-bd0b-08ade07fb82c"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Trang bị camera 50 MP cùng nhiều tính năng", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("0a80d9e0-4dd8-4fd0-b076-3048ee8a109c"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Camera chính của điện thoại có độ phân giải lên đến 50 MP, một con số ấn tượng đặc biệt trong phân khúc điện thoại giá rẻ. Việc chụp ảnh với độ phân giải cao như vậy cho phép mình bắt lấy từng chi tiết một cách rõ ràng và sắc nét, tạo ra những bức ảnh đầy ấn tượng và chân thực.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
