new GadgetDescription { Id = Guid.Parse("9ad65a03-48b4-44eb-89a9-0711d9984b3f"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Hiệu năng xử lý tốt các tác vụ cơ bản", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("fdfd1003-1b8c-48fe-8d65-deaa386e07cb"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Khi sử dụng vivo Y17s, mình cảm thấy hài lòng với hiệu suất mà con chip Helio G85 8 nhân mang lại. Con chip này được tối ưu hóa cho các sản phẩm giá rẻ. Với mức xung nhịp tối đa 2 GHz, điện thoại có thể xử lý các tác vụ cơ bản như lướt web, xem phim hay nhắn tin hằng ngày một cách mượt mà và không gặp phải khó khăn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
