new GadgetDescription { Id = Guid.Parse("57a545ee-7cce-49e3-9bf5-769ce838443c"), GadgetId = Guid.Parse("c1b12f63-cdea-432b-991c-c6b95fbd1724"), Value = "Khi mình sử dụng vivo Y17s, dung lượng pin lớn lên đến 5000 mAh thực sự đã làm mình cảm thấy thoải mái và tự tin hơn khi sử dụng điện thoại hằng ngày. Mình không cần phải lo lắng về việc pin sẽ xuống cấp quá nhanh khi mình đang sử dụng các ứng dụng nặng hoặc giải trí trong thời gian dài.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7e8b1b72-ff37-4c1f-bd32-bafbfadacf6d"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "vivo Y36, chiếc điện thoại dòng Y được nhà vivo tung ra thị trường Việt Nam vào tháng 06/2023. Máy sở hữu vẻ ngoài hiện đại và trẻ trung, màn hình hiển thị sắc nét cùng một hiệu năng ổn định với các tác vụ hằng ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("ba17e112-6d56-4381-8525-0c44ce1a2feb"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "Giải trí đã hơn trên màn hình 6.64 inch", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("d72e1e43-b7a4-4a7d-81d6-b63e918d459e"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "Màn hình trên vivo Y36 không phải là cái màn hình đỉnh cao, nhưng nó vẫn đáp ứng mượt mà cho các nhu cầu cơ bản của người dùng. Với tấm nền IPS LCD và độ phân giải Full HD+ (1080 x 2388 pixels), mỗi khi mình đắm chìm vào thế giới video trên vivo Y36, hình ảnh thể hiện độ chân thật, hỗ trợ góc nhìn rộng, mặc dù không thể so sánh với các màn hình cao cấp. Tuy nhiên, ở tầm giá này, màn hình vẫn đưa ra một trải nghiệm khá ổn định trong cùng phân khúc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("f08e4cc6-d168-46e3-9c73-84346c2f4202"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/2d476be5-4e64-4f5c-a48f-4652758cdc6e.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("4fc82749-86d7-46cb-8db4-8c311b252ae5"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "Hiệu năng ổn định cho nhiều tác vụ", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("f373d57f-f1b8-4cb4-a8aa-f09ee354725a"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "vivo Y36 được trang bị con chip Snapdragon 680, đây là vi xử lý tầm trung đến từ nhà Qualcomm có mức xung nhịp tối đa có thể đạt được theo như công bố của hãng là vào khoảng 2.4 GHz. Đối với những tựa game đang được ưa chuộng trên thị trường, vivo Y36 tiếp tục mang lại trải nghiệm hài lòng, đặc biệt là khi tham gia vào trò chơi Liên Quân Mobile. Việc chơi game trở nên mượt mà và ổn định, với sự linh hoạt trong thao tác di chuyển, vuốt skill, và hiệu ứng skill đều diễn ra một cách trôi chảy. Ngay cả trong những tình huống giao tranh sôi động, máy vẫn xử lý mọi thao tác một cách hiệu quả, tốc độ khung hình duy trì ở mức ổn định, đặc biệt là ở chất lượng đồ họa cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("a224a424-354f-4c48-9c06-fb3c714233e0"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/bd400b9f-70cd-421f-b9d5-6144f38dedfa.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
