new GadgetDescription { Id = Guid.Parse("1d8d37db-e7cb-4aca-9ed9-3a744ab9ac98"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "Theo mình thì phần camera của vivo Y36 đã hoàn thành rất tốt nhiệm vụ của mình khi cho ra những bức ảnh khá đẹp mắt trong hầu hết mọi điều kiện chụp. Để làm được điều đó cụm camera sau của máy được trang bị một cảm biến chính 50 MP và một cảm biến phụ để đo chiều sâu trường ảnh 2 MP.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
