new GadgetDescription { Id = Guid.Parse("47b679a4-32b7-4780-a445-c720561e9531"), GadgetId = Guid.Parse("429ab3f7-9518-491f-8181-b6fc1464a2e2"), Value = "Trong quá trình kiểm tra thời lượng pin của vivo Y36, với loạt bài kiểm tra từ việc chơi game, lướt web đến các thao tác cơ bản hằng ngày, tổng thời gian on-screen đạt hơn 9 tiếng. Đây là một khoảng thời gian sử dụng dài, và nếu thực hiện các tác vụ cơ bản, thì thời lượng pin sử dụng có thể lên đến 2 ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("5f048562-4fd8-48fd-9770-0697f5fa7430"), GadgetId = Guid.Parse("0958d091-4ec0-4a8e-9af0-74a79545ebb3"), Value = "vivo Y18 4GB/128GB là một chiếc smartphone từ thương hiệu nổi tiếng, được tối ưu hóa để mang lại trải nghiệm tốt nhất cho người dùng. Với dung lượng pin lớn 5000 mAh và hiệu năng mượt mà, sản phẩm này sẽ đáp ứng nhu cầu của những người dùng khó tính.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
