new GadgetDescription { Id = Guid.Parse("187570de-f222-4289-912c-c8484048c5fe"), GadgetId = Guid.Parse("693ca823-3ea0-4982-b4b2-d1e4ba43da4b"), Value = "vivo V29e 5G là một phiên bản điện thoại di động đáng chú ý trong phân khúc tầm trung, đặc biệt với sự kết hợp tinh tế giữa thiết kế và hiệu suất. Điều làm nổi bật chiếc điện thoại này chính là camera chất lượng, thiết kế sang trọng và viên pin lớn kèm sạc nhanh.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
