new GadgetDescription { Id = Guid.Parse("8413742f-e102-4703-a848-1ee085eba211"), GadgetId = Guid.Parse("f8b30f97-932b-4725-9d3f-dea18c3ca9ab"), Value = "Với nhiều tính năng chụp ảnh như: Chế độ chụp đêm, chụp ảnh xóa phông, chụp toàn cảnh và nhiều tính năng khác, chiếc điện thoại mở rộng khả năng sáng tạo của bạn, tạo ra những bức hình đẹp mắt và đa dạng. Các tính năng này không chỉ làm phong phú tùy chọn chụp ảnh mà còn mang lại trải nghiệm chụp hình đỉnh cao, đáp ứng nhanh chóng đa dạng nhu cầu sáng tạo của người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
