new GadgetDescription { Id = Guid.Parse("c815d7eb-ebea-40c0-aa2e-865c4e5f71c2"), GadgetId = Guid.Parse("99b6215b-d236-44a9-baf9-b80bbfb3ace5"), Value = "realme C51 (6GB/256GB) một chiếc điện thoại thông minh từ thương hiệu realme, với nhiều tính năng đáng chú ý và sức mạnh ấn tượng. Được thiết kế để đáp ứng nhu cầu đa dạng của người dùng hiện đại, realme C51 mang lại sự kết hợp hoàn hảo giữa hiệu suất, thiết kế thời trang và giá trị.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
