new GadgetDescription { Id = Guid.Parse("14370fd5-e105-4419-8ac5-1bbd4afd7ca5"), GadgetId = Guid.Parse("99b6215b-d236-44a9-baf9-b80bbfb3ace5"), Value = "realme C51 (6GB/256GB) được trang bị một viên pin với dung lượng lớn lên đến 5000 mAh, mang lại khả năng sử dụng lâu dài và không gặp sự cố gián đoạn trong quá trình sử dụng hằng ngày. Máy sử dụng loại pin Li-Po, không chỉ đảm bảo hiệu suất ổn định mà còn giúp tiết kiệm năng lượng và kéo dài tuổi thọ của pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("3bd66a65-b9a8-4bd9-934b-2e51a2f3affe"), GadgetId = Guid.Parse("00c42682-5d6e-48b6-bd28-ba0bf3e477d5"), Value = "realme 12 kết hợp giữa thiết kế đẹp và công nghệ hiện đại. Điện thoại có camera tròn lạ mắt, màn hình 120 Hz mượt mà, chip Snapdragon 685 mạnh mẽ, cùng với pin 5000 mAh và sạc nhanh 67W, mang đến trải nghiệm hoàn hảo cả về hiệu suất và ngoại hình.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
