new GadgetDescription { Id = Guid.Parse("3600f36d-86dd-44bb-aa2c-bf1b6d63e634"), GadgetId = Guid.Parse("00c42682-5d6e-48b6-bd28-ba0bf3e477d5"), Value = "Chiếc điện thoại realme này được trang bị viên pin khủng 5000 mAh, mang đến thời gian trải nghiệm thoải mái, để bạn yên tâm sử dụng cả ngày không lo ngắt quãng. Đặc biệt, với sạc nhanh SuperVOOC 67W, chỉ cần 19 phút là pin tăng từ 1% lên 50% và sau 47 phút, pin đã đầy 100% (theo công bố từ hãng). Đây thực sự là lựa chọn lý tưởng cho những ai luôn bận rộn, vừa tiết kiệm thời gian lại vừa duy trì sự liên tục trong trải nghiệm sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
