new GadgetDescription { Id = Guid.Parse("2905cfb8-da65-417c-a983-72b08cf3aead"), GadgetId = Guid.Parse("388e0c15-6580-4966-bc53-ae55ce9bdd51"), Value = "HONOR X6b 6GB/128GB Tím là chiếc điện thoại thông minh nổi bật trong phân khúc giá rẻ, với sự kết hợp giữa hiệu năng mạnh mẽ, camera AI 50 MP sắc nét và pin dung lượng lớn 5200 mAh. Thiết kế hiện đại cùng nhiều tính năng tiện ích giúp HONOR X6b mang lại trải nghiệm tuyệt vời cho người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
