new GadgetDescription { Id = Guid.Parse("6f384d5b-06e2-4710-94e6-387e57888f66"), GadgetId = Guid.Parse("388e0c15-6580-4966-bc53-ae55ce9bdd51"), Value = "Sở hữu viên pin 5200 mAh, HONOR X6b mang đến dung lượng lớn so với nhiều smartphone hiện nay. Viên pin khủng như này giúp người dùng sử dụng thiết bị cả ngày mà không phải lo lắng về việc sạc lại. Điều này cực kỳ hữu ích cho những ai có nhu cầu sử dụng điện thoại nhiều cho công việc, học tập và giải trí.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
