new GadgetDescription { Id = Guid.Parse("1870542f-0c1e-46dd-8dfa-1910901af87b"), GadgetId = Guid.Parse("388e0c15-6580-4966-bc53-ae55ce9bdd51"), Value = "HONOR X6b trang bị bộ xử lý Helio G85 của MediaTek, nổi bật trong phân khúc với hiệu năng mạnh mẽ. Helio G85 có 8 lõi, trong đó 2 lõi hiệu suất cao chạy ở 2.0 GHz và 6 lõi tiết kiệm năng lượng chạy ở 1.8 GHz. Cấu trúc này cho phép thiết bị dễ dàng xử lý các tác vụ hằng ngày như lướt web, xem video và chơi game.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
