new GadgetDescription { Id = Guid.Parse("9272e2be-2ef6-4303-bfc9-fa195de7e68a"), GadgetId = Guid.Parse("388e0c15-6580-4966-bc53-ae55ce9bdd51"), Value = "Chiếc điện thoại HONOR này sở hữu camera chính 50 MP với sự hỗ trợ của công nghệ AI, giúp ghi lại hình ảnh sắc nét và chi tiết, ngay cả khi zoom lên. Camera này rất thích hợp cho việc chụp ảnh phong cảnh, kiến trúc và chân dung, với khả năng giảm nhiễu và tăng độ chi tiết trong điều kiện ánh sáng yếu, đảm bảo ảnh luôn đẹp và rõ nét trong mọi điều kiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e7fb5570-65e5-4958-90e7-af0a2cc3f8b6"), GadgetId = Guid.Parse("d3f678be-805c-4a32-80be-9a107d73be8f"), Value = "Thị trường smartphone ngày càng biến đổi và cạnh tranh, HONOR X7b chắc chắn sẽ là một cái tên không thể bỏ qua. Với sự đầu tư đáng kể vào các yếu tố thiết kế, cấu hình và camera, sản phẩm tạo ra một dấu ấn đáng nhớ trên thị trường điện thoại thông minh.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
