new GadgetDescription { Id = Guid.Parse("95f06329-1b8c-406f-8e76-0cf3e1f1e5d2"), GadgetId = Guid.Parse("d3f678be-805c-4a32-80be-9a107d73be8f"), Value = "Màn hình của HONOR X7b được trang bị tấm nền TFT LCD, đảm bảo hiển thị màu sắc rõ ràng và chi tiết. Với độ phân giải Full HD+ (1080 x 2412 Pixels), hình ảnh trên màn hình trở nên cực kỳ sắc nét. Sự kết hợp này giữa tấm nền TFT LCD và độ phân giải cao giúp màu sắc trên màn hình luôn trung thực và sống động.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
