new GadgetDescription { Id = Guid.Parse("65c95867-e726-4f85-85e4-f3c4adfbb543"), GadgetId = Guid.Parse("d3f678be-805c-4a32-80be-9a107d73be8f"), Value = "Chiếc điện thoại được trang bị bộ vi xử lý Snapdragon 680 8 nhân, nằm trong phân khúc giá rẻ từ nhà sản xuất chip nổi tiếng Qualcomm. Dù đã có một thời gian ra mắt, nhưng Snapdragon 680 vẫn cho thấy hiệu suất ổn định trong việc xử lý các nhiệm vụ hằng ngày như duyệt web, nghe nhạc hay xem video.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
