new GadgetDescription { Id = Guid.Parse("1456fd15-ffdf-493b-ae3c-9d7b223bc3d0"), GadgetId = Guid.Parse("d3f678be-805c-4a32-80be-9a107d73be8f"), Value = "HONOR X7b được trang bị viên pin dung lượng 6000 mAh, là một trong những ưu điểm nổi bật nhất của chiếc smartphone này. Với dung lượng pin lớn cho phép người dùng không cần phải mang theo bộ sạc. Bạn có thể thoải mái xem video, lướt web, chụp ảnh hay chơi game liên tục mà không lo ngại máy sớm hết pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("b8671b68-f530-409e-80aa-800d2a92ead5"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "HONOR 200 5G 12GB/256GB là lựa chọn lý tưởng cho những ai yêu thích chụp ảnh với độ sắc nét ấn tượng và nhiều chế độ lung linh. Máy có màn hình sống động kết hợp cùng khả năng chụp ảnh chuyên nghiệp, đảm bảo mọi khoảnh khắc đều được ghi lại hoàn hảo.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1ae79437-2a10-4025-b5f1-4e0230d997e5"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Hiển thị AMOLED rực rỡ với độ sáng 4000 nits", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("9ad2084d-b2c4-4a6e-ac55-b2a711ab4560"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Sản phẩm sở hữu màn hình AMOLED 6.7 inch rực rỡ, sắc nét, cho độ tương phản ấn tượng khiến hình ảnh trở nên sống động hơn bao giờ hết. Thiết kế cong tràn viền không chỉ mở rộng tầm nhìn mà còn mang đến cảm giác như bạn đang đắm mình trong từng cảnh phim hay trận game, tận hưởng mọi chi tiết một cách rõ ràng và chân thật.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("da28dc95-bf9d-44bb-b062-f81bd65e2bb7"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/915c2feb-a29a-461f-b655-a35a00f5c76c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d17695a2-c5df-4b07-b08f-eea19e12528b"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Vi xử lý mạnh mẽ giúp bạn chơi game trơn tru và mượt mà", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("933ee38e-2954-43ae-85ef-15d599c4255c"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Nhờ vi xử lý Snapdragon 7 Gen 3 và Adreno 720, HONOR 200 mang đến trải nghiệm chơi game vượt trội. Những tựa game nặng chạy mượt mà, phản hồi tức thì, hạn chế giật lag, đảm bảo từng pha hành động diễn ra liền mạch. Hiệu ứng đồ họa sắc nét, không còn tình trạng đứng máy hay thoát game bất ngờ, giúp bạn hoàn toàn đắm chìm trong trận đấu.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
