new GadgetDescription { Id = Guid.Parse("f2e87d73-22e9-40b7-bc2a-57924e2c0b99"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Camera hiện đại với công nghệ hỗ trợ chụp ảnh chuyên nghiệp", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("0eb16578-6bee-4521-83db-f8f9d426952d"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Hệ thống camera sau của HONOR 200 với camera chính 50 MP, camera tele 50 MP và camera 12 MP hỗ trợ chụp macro và góc siêu rộng, giúp tạo ra hình ảnh đẹp dễ dàng. Máy cung cấp khả năng chống rung, zoom quang học và kỹ thuật số rõ nét, cùng nhiều chế độ chụp đa dạng. Camera selfie 50 MP trên HONOR 200 không chỉ mạnh về megapixel mà còn tối ưu ánh sáng chân dung, mang đến ảnh selfie sống động. Hỗ trợ quay video 4K và tính năng xóa phông giúp bạn chụp ảnh chân dung ấn tượng như nhiếp ảnh gia.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("5e5a32b9-b9aa-4246-9edb-582707d1abb2"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/a145193b-d3a7-429f-9f06-a1f6ae2a4418.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("3a3edd15-d146-4f27-9132-41fdb7662e2d"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Sử dụng cả ngày với pin lớn và sạc siêu tốc", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("fe35ae3f-0eff-4d68-b37a-5c9eed0e8257"), GadgetId = Guid.Parse("de26794e-a2e7-4d93-bb27-197c17292220"), Value = "Nếu bạn tìm kiếm trải nghiệm chơi game thả ga và chụp ảnh không gián đoạn, thì thiết bị này chính là lựa chọn lý tưởng. Với viên pin 5200 mAh mạnh mẽ, HONOR đảm bảo bạn có thể giải trí và làm việc suốt cả ngày mà không phải lo về việc cạn pin. Không còn lo lắng về việc sạc pin quá lâu, nhờ khả năng sạc lên đến 100W của điện thoại sạc siêu nhanh, chỉ cần 15 phút, bạn có thể sạc từ 0% đến 49% (theo thông tin hãng cung cấp). Công nghệ sạc nhanh này không chỉ giúp tiết kiệm thời gian của người dùng mà còn đảm bảo rằng sản phẩm luôn sẵn sàng khi cần.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("de68bc5a-52b2-467e-aef1-da7a9dd70582"), GadgetId = Guid.Parse("4a373ccc-0884-4884-bbca-c5338be18c84"), Value = "Trong thế giới điện thoại di động ngày nay, tìm một chiếc smartphone vừa túi tiền nhưng vẫn chất lượng không phải là điều dễ dàng. TCL 406s, một lựa chọn mới nổi hứa hẹn sẽ thay đổi suy nghĩ đó với những tính năng ấn tượng không kém cạnh bất kỳ chiếc điện thoại cao cấp nào.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
