new GadgetDescription { Id = Guid.Parse("ae34d8e2-9b34-4067-aaba-d966c88e1c45"), GadgetId = Guid.Parse("d20ed725-cf4a-482b-94e2-755e4ca380fc"), Value = "Pin khủng dùng thoải mái cả ngày", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("ee22e013-a95c-40f6-98c5-ee55a0c390e6"), GadgetId = Guid.Parse("d20ed725-cf4a-482b-94e2-755e4ca380fc"), Value = "Tecno Spark Go 2024 được trang bị viên pin dung lượng 5000 mAh, một điểm mạnh nổi bật giúp người dùng yên tâm sử dụng mà không cần lo về vấn đề sạc pin quá thường xuyên. Với dung lượng pin lớn như vậy, Spark Go 2024 có khả năng hoạt động bền bỉ hơn 900 giờ ở chế độ chờ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("e41a1bad-f8a6-4d5e-addc-c5ba85cd3aab"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "Bạn đang tìm kiếm một chiếc smartphone có khả năng đáp ứng mọi nhu cầu từ công việc đến giải trí, đồng thời không làm bạn thất vọng về thiết kế và hiệu suất. Sản phẩm Tecno Spark 20C không chỉ sở hữu ngoại hình bắt mắt mà còn được trang bị những tính năng ưu việt, hứa hẹn mang lại trải nghiệm tuyệt vời nhất.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b11f97ad-8bdb-406a-ad5d-e06721e30c77"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "Màn hình hiển thị chất lượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b085bb8d-ab37-43f9-bc85-364472374eb0"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "Được trang bị tấm nền IPS LCD, Tecno Spark 20C hiển thị nổi bật với màu sắc trung thực và góc nhìn rộng. Hỗ trợ độ phân giải HD+ (720 x 1612 Pixels), tuy không quá cao nhưng vẫn giúp cho từng chi tiết trở nên sắc nét trong tầm giá. Tấm nền này cũng có độ bền cao và tiết kiệm năng lượng hiệu quả.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("f205e542-c8b2-44ba-a851-2609b1353743"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/c4dcc4b8-8724-4ec2-8989-491f7ac96237.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
