new GadgetDescription { Id = Guid.Parse("d8362490-6622-41ca-b110-be0d195e9c59"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "Tecno Spark 20C sử dụng bộ vi xử lý Helio G36 8 nhân mạnh mẽ, được thiết kế để đáp ứng tốt các tác vụ hằng ngày và một số tựa game không yêu cầu đồ hoạ quá cao. Với 8 GB RAM, Tecno Spark 20C giúp bạn sử dụng nhiều ứng dụng cùng lúc mà không giật lag, duy trì hiệu suất và chuyển đổi giữa các ứng dụng mượt mà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
