new GadgetDescription { Id = Guid.Parse("4cb7cb1b-3535-467f-9b4b-f2f7d6d6e18f"), GadgetId = Guid.Parse("b7e54ef7-e96b-40f2-84ba-49144c3d3ec3"), Value = "Với viên pin 5000 mAh, Tecno Spark 20C có thể đồng hành cùng bạn suốt cả ngày dài sử dụng, từ việc lướt web, xem video, cho đến chơi game hay sử dụng các ứng dụng nặng mà không hề gây ra cảm giác việc pin sẽ nhanh chóng cạn kiệt.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("4ab82940-d457-4699-817f-8b64f687f994"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Nokia 220 4G là chiếc điện thoại phổ thông có một thiết kế bền bỉ, nhẹ nhàng và hiện đại. Với màn hình 2.8 inch rõ nét, hỗ trợ 4G VoLTE cho chất lượng cuộc gọi cao, cùng nhiều tiện ích thông minh, đây chính là lựa chọn đáng giá cho mọi nhu cầu liên lạc hằng ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("79e57311-15b8-43fa-8540-710534579641"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Vẻ ngoài vừa hiện đại vừa truyền thống", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("46107e35-dbaf-4bf8-b8fc-4c0034cfadc0"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Nokia 220 4G được thiết kế theo phong cách vuông vức, hiện đại, với chất liệu nhựa nhẹ nhàng, chỉ nặng 98 g. Điểm nhấn đặc biệt của máy là mặt lưng có hoạ tiết kẻ sọc, tạo cảm giác chắc chắn khi cầm nắm, không lo trơn trượt. Bàn phím dạng T9 truyền thống được làm phẳng hơn, mang lại cảm giác quen thuộc cho người sử dụng mặc dù có thể hơi khó khăn cho những ai quen dùng phím nổi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("01db630b-4ffb-4c65-866f-d0698935f723"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Màn hình to 2.8 inch, dễ nhìn", Type = ParseGadgetDescriptionType("BoldText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("5c3b23d6-ef8c-4d4e-beea-7006bbeede07"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Màn hình 2.8 inch của Nokia 220 4G to rõ, dễ nhìn, đặc biệt hữu ích cho người lớn tuổi. Kích thước màn hình này giúp nội dung hiển thị rõ ràng, không cần căng mắt, mang lại trải nghiệm sử dụng thuận tiện và thoải mái.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("52c672a5-98a3-4a89-b731-558a8eb7ace3"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Nghe gọi chất lượng cao với 4G VoLTE", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("057bfab1-e80f-4239-9460-1ee38d1fdc26"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Nokia 220 4G hỗ trợ 2 Nano SIM và công nghệ 4G VoLTE, đảm bảo chất lượng cuộc gọi to rõ, không nhiễu. Điều này giúp người dùng duy trì liên lạc hiệu quả, tiện lợi cho việc sử dụng số riêng cho công việc và gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("450135f2-9ad9-424e-9750-dfa70bcdd1fb"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Pin bền bỉ, dùng lâu nhiều ngày", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("a8b91ddc-24f2-491a-8b82-c288e7f89f6a"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Với viên pin dung lượng 1450 mAh, Nokia 220 4G có thể hoạt động liên tục trong nhiều ngày mà không lo cạn nguồn nhanh chóng. Đây là một ưu điểm lớn với những ai cần một thiết bị dùng lâu mà không cần sạc thường xuyên.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("1915cfef-1114-4618-9b4c-792cade86309"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Một số tiện ích khác", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("903bed74-1ba8-47df-a75e-62485619d1a8"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Chiếc điện thoại Nokia này tích hợp nhiều tiện ích hiện đại như cổng sạc Type-C giúp sạc nhanh chóng và tiện lợi. Máy còn có cổng jack 3.5 mm cho tai nghe, đèn pin tiện ích trong tình huống khẩn cấp và tính năng nghe radio mang đến trải nghiệm giải trí đáng giá. Những tiện ích này đảm bảo người dùng có mọi thứ cần thiết cho cuộc sống hằng ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("e14b5b0d-e40e-48d4-bc65-501cfc8f0478"), GadgetId = Guid.Parse("03741d7c-f36f-4cb7-8a2d-d746587096be"), Value = "Nokia 220 4G không chỉ đáp ứng nhu cầu cơ bản về gọi điện và nhắn tin, mà còn là chiếc điện thoại thông minh với nhiều tính năng hữu ích, phù hợp cho mọi lứa tuổi từ người trẻ đến người lớn tuổi. Đây chắc chắn là chiếc điện thoại đáng để bạn xem xét và trải nghiệm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("ffca6b9e-525a-4c69-b017-624baa7f3e76"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Đi cùng với sự phát triển của những chiếc điện thoại thông minh thì mẫu điện thoại “cục gạch” cũng được các nhà sản xuất trao chuốt. Và Nokia 105 4G Pro mẫu điện thoại phổ thông được Nokia cho ra mắt, máy mang một thiết kế đơn giản, dễ sử dụng, khả năng nghe gọi rõ ràng và thời gian sử dụng dài lâu.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("22fec677-a3d4-4104-9918-3376c088029d"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Thiết kế đơn giản, dễ dàng thao tác", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("87c3198f-abeb-40cc-84ce-b04c2c408c1d"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Một phong cách thiết kế quen thuộc vẫn được giữ nguyên trên Nokia 105 4G Pro, nhưng đã được điều chỉnh để mang lại vẻ trẻ trung và nhẹ nhàng. Mặt lưng được bo tròn mềm mại, tạo cảm giác cầm nắm thoải mái hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
