new GadgetDescription { Id = Guid.Parse("d89258cd-f9fe-4d7f-9b1d-3a5b688d460e"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Nokia 105 4G Pro được trang bị màn hình 1.77 inch cùng tấm nền IPS LCD, với độ phân giải QQVGA (120 x 160 Pixels), mang lại chất lượng hình ảnh tốt và màu sắc trung thực. Điều này giúp hiển thị phông chữ và biểu tượng với độ chi tiết tốt, giúp bạn dễ dàng nhìn và thao tác một cách thuận tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("fea440e0-035d-4143-a8e9-f47e02a2e126"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/3a1fe86d-123f-4d34-8587-2d61c626e56c.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("5bb95596-670b-47fd-91eb-ccae1a995363"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Nghe gọi rõ ràng với âm thanh to lớn", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("67718189-4b50-4942-baaf-365dfd84b3a6"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Nokia trang bị cho máy 2 nano sim, giúp bạn tận dụng một cho công việc, một cho gia đình hoàn toàn tiện lợi. Với 4G LTE hỗ trợ cho bạn chất lượng cuộc gọi to, rõ ràng không bị nhiễu sóng, chập chờn trừ những nơi không phủ sóng điện thoại. Máy được trang bị RAM 48 MB cùng bộ nhớ trong 128 MB, giúp lưu trữ một vài thông tin và các loại tệp nhỏ. Điều tuyệt vời là bạn hoàn toàn có thể mở rộng dung lượng lưu trữ thông qua thẻ nhớ MicroSD tối đa 32 GB. Điều này cho phép bạn thoải mái và thưởng thức những bản nhạc, video ưa thích mọi lúc mọi nơi mà không lo bị hạn chế không gian lưu trữ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9e3362bd-159b-4292-9668-6df8f5528fdb"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/bbc1fa28-241a-4770-87ad-cad7f833e9eb.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c1b2e249-3501-408f-8933-e8f931bbdd83"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Dùng lâu liên tục không lo hết pin", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("736ae0b8-4bdd-447c-92ba-a1e7a6e1c504"), GadgetId = Guid.Parse("3482e407-8c2b-4de8-9e38-6e6c44dbb624"), Value = "Nokia 105 4G Pro được trang bị viên pin rời dung lượng 1450 mAh. Điều này hứa hẹn cung cấp thời gian sử dụng lâu dài mà không lo về việc hết pin khi bạn đang sử dụng. Với dung lượng pin này, bạn có thể yên tâm sử dụng điện thoại trong nhiều ngày liền mà không cần phải tìm kiếm nguồn điện để sạc.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("6c328052-fefa-46e6-965d-414f4ef87759"), GadgetId = Guid.Parse("336c67cc-5bf3-44c1-9798-f7241c892e68"), Value = "Trong bối cảnh mạng 2G đang dần bị loại bỏ và nhiều người vẫn chưa quen với các smartphone hiện đại, Nokia HMD 105 4G chính là giải pháp hợp lý. Đây là chiếc điện thoại phổ thông, vừa giữ được sự quen thuộc với bàn phím T9 cổ điển, vừa hỗ trợ mạng 4G để đảm bảo kết nối thông suốt trong thời kỳ chuyển đổi số.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
