new GadgetDescription { Id = Guid.Parse("d25f6776-63bb-4544-83c5-a2c9ea45741b"), GadgetId = Guid.Parse("336c67cc-5bf3-44c1-9798-f7241c892e68"), Value = "Thiết kế của Nokia HMD 105 4G đơn giản nhưng tinh tế. Với khối lượng chỉ 88g, chiếc điện thoại này cực kỳ nhẹ nhàng, mang lại cảm giác thoải mái khi cầm nắm. Điểm cộng là pin rời dễ dàng thay thế, rất hữu ích cho người dùng không muốn phiền toái trong việc bảo dưỡng pin. Vỏ máy làm từ nhựa, vừa bền bỉ vừa giúp giảm khối lượng và bạn có thể lựa chọn giữa ba màu sắc cá tính: hồng, đen và xanh mint.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
