new GadgetDescription { Id = Guid.Parse("047ad3e6-aa1f-46f5-8f53-24d496b43043"), GadgetId = Guid.Parse("336c67cc-5bf3-44c1-9798-f7241c892e68"), Value = "Về pin, chiếc điện thoại Nokia này có dung lượng 1450 mAh chính là một điểm mạnh, cho phép sử dụng liên tục trong nhiều ngày mà không phải sạc thường xuyên. Cổng sạc Type-C hiện đại càng làm cho quá trình sạc nhanh chóng và tiện lợi hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("055a2346-cd09-47f0-bfd1-346825703a32"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Nokia 3210 4G, một biểu tượng trường tồn của hãng Nokia, nay trở lại với vẻ ngoài cải tiến và công nghệ hiện đại. Đây là sự kết hợp giữa thiết kế hoài cổ và những tính năng tiên tiến, đáp ứng mọi nhu cầu cơ bản của người dùng.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
