new GadgetDescription { Id = Guid.Parse("5f94a21c-e92c-4263-ba00-df7151b79b81"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Nokia 3210 4G gây ấn tượng mạnh với việc hoàn thiện tỉ mỉ đến từng chi tiết, mang lại cảm giác sang trọng và truyền thống. Với chất liệu nhựa cao cấp, máy chỉ nặng 88 g, nhẹ nhàng và thoải mái khi cầm nắm. Thiết kế hơi phình to ở giữa gợi nhớ đến những dòng điện thoại kinh điển của Nokia, vừa hiện đại vừa mang nét hoài cổ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("678434a3-fbb7-4c93-bfb3-52e501f295d8"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Bàn phím T9 quen thuộc", Type = ParseGadgetDescriptionType("BoldText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("d568b280-25e7-4394-8a1c-59148e30fb0e"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Máy sử dụng bàn phím T9 truyền thống, được thiết kế nổi giúp người dùng dễ dàng nhắn tin và gọi điện. Sự quen thuộc với bàn phím T9 sẽ mang lại trải nghiệm sử dụng thuận tiện hơn, đặc biệt là với những người đã từng quen dùng các dòng điện thoại cổ điển.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("2bd4cff1-a575-48e9-a222-66636b0973df"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Màn hình chất lượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("4cfc772d-8c96-4b36-9011-fef5995710f1"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Nokia 3210 4G có màn hình 2.4 inch, vừa đủ để hiển thị các thông tin cần thiết với chất lượng tương đối tốt. Tuy nhiên, để có trải nghiệm tốt nhất, người dùng nên hạn chế sử dụng máy ở những nơi có nguồn sáng quá cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
