new GadgetDescription { Id = Guid.Parse("a60444a2-7c2f-4ce6-a6de-f43951ce5839"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Máy hỗ trợ 2 Nano SIM, giúp người dùng dễ dàng quản lý cuộc sống cá nhân và công việc. Công nghệ 4G VoLTE tiên tiến đảm bảo cho các cuộc đàm thoại được to rõ và không bị rè nhiễu, mang lại sự hài lòng tuyệt đối cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("9b22dda2-67ce-4bc4-b002-6943bf9de86c"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Tiện ích kèm theo", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("c4841ad3-7c00-4fd9-a8eb-5b625f826625"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Ngoài các tính năng cơ bản, chiếc điện thoại Nokia này còn được trang bị nhiều tiện ích hiện đại. Cổng sạc Type-C giúp việc sạc pin trở nên nhanh chóng và thuận tiện hơn. Jack cắm 3.5 mm cho phép bạn kết nối tai nghe một cách dễ dàng. Ngoài ra, bạn còn có thể nghe radio và nhạc MP3 để giải trí trong những lúc rảnh rỗi. Đèn pin luôn sẵn sàng chiếu sáng bất cứ khi nào bạn cần.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("7102c0bf-e35d-40b8-8fab-fc47f680b18f"), GadgetId = Guid.Parse("5c39b445-4afd-4042-af85-0c3f245380ef"), Value = "Điện thoại phổ thông Nokia 3210 4G vượt xa mong đợi với sự kết hợp giữa những yếu tố cổ điển và công nghệ hiện đại. Với thời lượng pin ấn tượng, chất lượng đàm thoại vượt trội và những tiện ích đa dạng, đây thực sự là lựa chọn lý tưởng cho mọi đối tượng, từ người dùng cơ bản đến những ai yêu thích sự hoài niệm nhưng cũng mong muốn sự tiện ích hiện đại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("53a52b5c-698f-4493-af9b-1fd5f72ce43c"), GadgetId = Guid.Parse("c465f5fd-bc32-47cb-b630-5aec7f7967f0"), Value = "Nokia 110 4G Pro là một chiếc điện thoại phổ thông nổi bật, được thiết kế tinh tế và sang trọng. Điện thoại cho khả năng bền bỉ trong thời gian dài khiến bạn cảm thấy yên tâm khi sử dụng. Với việc hỗ trợ công nghệ 4G VoLTE, bạn có thể thực hiện cuộc gọi một cách nhanh chóng và ổn định hơn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("b1f5bcc6-89a8-44f2-a418-9dcf7d6cd9ab"), GadgetId = Guid.Parse("c465f5fd-bc32-47cb-b630-5aec7f7967f0"), Value = "Diện mạo đơn giản cuốn hút ánh nhìn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("c0c20e39-3d58-456b-aee6-929ecf9b83c1"), GadgetId = Guid.Parse("c465f5fd-bc32-47cb-b630-5aec7f7967f0"), Value = "Máy được làm từ nhựa với vỏ sáng bóng, tạo vẻ đẹp sang trọng và tinh tế. Điểm cộng trên những mẫu điện thoại phổ thông Nokia là độ bền cao, giảm thiểu nguy cơ nứt vỡ do va chạm hoặc rơi vỡ. Điện thoại Nokia do được sản xuất từ nhựa, mang lại khối lượng nhẹ và kích thước gọn gàng, thuận tiện cho việc bảo quản, sử dụng và di chuyển. Sử dụng để nghe và gửi tin nhắn, bạn sẽ cảm nhận được sự thoải mái khi cầm nắm.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
