new GadgetDescription { Id = Guid.Parse("543ea320-3d2c-4cc8-9824-c8925bdc336e"), GadgetId = Guid.Parse("ee170602-6293-4432-b62c-ab330797d994"), Value = "Máy được trang bị viên pin 1020 mAh, tương đối lớn đối với điện thoại phổ thông, bạn có thể sử dụng liên tục khoảng 3-5 ngày thoải mái tùy vào tần suất sử dụng. Nokia 105 4G, chiếc điện thoại mang đến nhiều giá trị cho người dùng, về công nghệ 4G hiện đại hay các nhu cầu liên lạc hoặc thậm chí là giải trí cơ bản, thì Nokia 105 4G chắc chắn sẽ không làm thất vọng người tin dùng khi lựa chọn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
