new GadgetDescription { Id = Guid.Parse("59cced30-97de-4328-8879-b54427123f5e"), GadgetId = Guid.Parse("ee170602-6293-4432-b62c-ab330797d994"), Value = "Toàn thân của máy được hoàn thiện từ nhựa với lớp vỏ sáng bóng nhìn khá đẹp mắt, tạo sự sang trọng và tinh tế cho máy. Một trong những điểm cộng của thiết bị là độ bền cao hạn chế được tình trạng nứt vỡ khi vô tình va chạm hoặc làm rơi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("2152f9b2-9fb3-445c-bb4e-810592ef94fe"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Bạn đang tìm kiếm một chiếc điện thoại cơ bản, dễ sử dụng, tập trung vào nhu cầu nghe và gọi là chủ yếu. Và Masstel Fami 50 4G tỏa sáng như một lựa chọn đơn giản nhưng mạnh mẽ, thực sự phù hợp với người lớn tuổi hoặc những ai muốn sở hữu một chiếc điện thoại phụ cho nhu cầu nghe gọi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("8f1a3187-95b5-4988-92a9-484dc77e9104"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Thiết kế đơn giản, tinh tế", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("86c2bf57-d6fa-43a9-8945-a0fa0ac10966"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Masstel Fami 50 4G được thiết kế với tiêu chí đơn giản mà chất lượng. Vỏ máy chủ yếu là nhựa, không chỉ nhẹ nhàng mà còn tăng thêm tiện nghi khi cầm nắm. Sự nhỏ gọn mang lại vẻ ngoài thanh lịch mà còn làm cho việc sử dụng hằng ngày trở nên thuận tiện hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("a9914ac6-6fe9-40d0-8497-d74c6f6df712"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Bàn phím T9 to rõ", Type = ParseGadgetDescriptionType("BoldText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("a9445892-8434-49fb-a520-00246f67e994"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Điểm nổi bật của Masstel Fami 50 4G chính là bàn phím T9 lớn, được làm nổi. Điều này không chỉ tạo ra hình ảnh thân thiện với người lớn tuổi, mà còn giúp việc nhắn tin và gọi điện trở nên dễ dàng, giảm thiểu sự nhầm lẫn khi nhấn phím.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("e5ee76ba-8f6e-40b2-a2c7-c0d5e43fdd1d"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Độ bền pin ấn tượng", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("661a9dcb-44f5-4c51-9e7a-2e3b39bc4708"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Pin của chiếc điện thoại Masstel này có dung lượng 1750 mAh, một con số ấn tượng trong phân khúc điện thoại cơ bản, đảm bảo duy trì hoạt động trong nhiều ngày mà không cần sạc thường xuyên. Cổng sạc Type-C cũng góp phần làm tăng tốc độ và sự tiện lợi trong việc sạc, một sự kết hợp hài hòa giữa truyền thống và hiện đại.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("4c0b85a2-42da-4c52-b964-f79794e5cc77"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Chất lượng cuộc gọi đảm bảo", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("3c1820ce-906a-4b02-8f7a-2ed1e6604e1f"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Khả năng hỗ trợ 4G VoLTE giúp nâng cao chất lượng cuộc gọi, với âm thanh to rõ, giảm thiểu tiếng ồn bên ngoài, mang đến trải nghiệm đàm thoại thoải mái nhất cho người dùng. Việc hỗ trợ hai Nano SIM cũng tạo điều kiện thuận lợi để quản lý cuộc gọi và dữ liệu dễ dàng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("7b1d8b28-dce9-45fc-af8e-80be24f912a1"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Tiện ích đa dạng", Type = ParseGadgetDescriptionType("BoldText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("4415bde3-7133-4a0c-bccf-5ec133647f5a"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Mặc dù tập trung vào những chức năng cơ bản, Masstel Fami 50 4G không hề thiếu những tiện ích giải trí. Tính năng FM không cần tai nghe là một điểm cộng lớn giúp người dùng dễ dàng thưởng thức sóng radio ở bất cứ đâu, trong khi khả năng hỗ trợ thẻ MicroSD lên đến 32 GB cho phép lưu trữ lượng lớn dữ liệu và tệp nhạc MP3, cho giải trí không giới hạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("0c5c9fb0-f40a-4bef-b350-ab1d8720c06d"), GadgetId = Guid.Parse("f1561d3f-9d02-4f8a-ae13-770dfadac306"), Value = "Masstel Fami 50 4G với thiết kế nhỏ gọn, bàn phím to rõ, pin dung lượng cao và chất lượng cuộc gọi ổn định, nó là sự lựa chọn lý tưởng cho những người cần một chiếc điện thoại đáng tin cậy, dễ sử dụng và tập trung vào nhu cầu nghe gọi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("7278f535-1eba-401b-9adf-4d024bee0f00"), GadgetId = Guid.Parse("eff0fd10-02a7-476a-b1b0-1a79e7d69b0c"), Value = "Trong thời đại smartphone ngày càng trở nên phổ biến, Masstel IZI T6 4G như làn gió mới cho những ai mong muốn trở về với sự đơn giản, tiện ích trong sử dụng hằng ngày. Điện thoại là sự lựa chọn lý tưởng cho nhóm khách hàng có nhu cầu cơ bản như nghe gọi và nhắn tin, đặc biệt phù hợp với người lớn tuổi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("51e56f0b-7ae9-4c34-9ec7-4a81db6b9513"), GadgetId = Guid.Parse("eff0fd10-02a7-476a-b1b0-1a79e7d69b0c"), Value = "Thiết kế nhỏ gọn và dễ sử dụng", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1ec531f6-fa49-4374-9aa1-1f7fecc26e5d"), GadgetId = Guid.Parse("eff0fd10-02a7-476a-b1b0-1a79e7d69b0c"), Value = "Chiếc điện thoại Masstel này được thiết kế nhỏ gọn, dễ cầm nắm, là bạn đồng hành lý tưởng cùng bạn mọi nơi, mọi lúc. Với bàn phím T9 dạng nổi, việc nhấn phím trở nên dễ dàng và thoải mái hơn bao giờ hết, đảm bảo cảm giác nhập số hoặc nhắn tin mượt mà, đặc biệt với người lớn tuổi. Không dừng lại ở đó, thiết kế pin rời mang lại khả năng thay thế dễ dàng khi pin đã đến lúc cần được thay mới.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
