new GadgetDescription { Id = Guid.Parse("726d46e1-9709-4a40-a1dc-a012e11a84f3"), GadgetId = Guid.Parse("eff0fd10-02a7-476a-b1b0-1a79e7d69b0c"), Value = "Điện thoại hỗ trợ hai nano SIM, kết nối 4G VoLTE - công nghệ giúp nâng cao chất lượng cuộc gọi. Bên cạnh đó, khả năng lưu trữ lên đến 2000 số điện thoại giúp bạn quản lý danh bạ một cách dễ dàng, không lo lắng về việc hết không gian.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
