new GadgetDescription { Id = Guid.Parse("3ece71c0-e2c0-40d6-a80c-89acb4e32263"), GadgetId = Guid.Parse("f3f137df-02b3-447b-8cac-dbcc5a32b1b2"), Value = "Masstel IZI 10 4G là 1 phiên bản \"cục gạch\" khác của nhà Masstel có tích hợp 4G mang lại trải nghiệm nghe gọi cực tốt, cùng thiết kế thân thiện dễ dùng, có hỗ trợ khe cắm thẻ nhớ và dung lượng pin tốt, rất đáng lựa chọn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
