new GadgetDescription { Id = Guid.Parse("c4a02b56-e8fd-43a7-915e-05ec57506c25"), GadgetId = Guid.Parse("f3f137df-02b3-447b-8cac-dbcc5a32b1b2"), Value = "Cùng với hệ bàn phím có phím nhấn lớn, rõ ràng, nhấn nhạy và êm, thao tác soạn thảo nhanh chóng, tiện lợi, các chức năng cơ bản thiết thực, nhanh chóng giúp người dùng làm quen ngay lần đầu sử dụng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("91a5e0da-9d8f-43cc-bac3-7235408d33a7"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Trong thế giới di động ngày nay, việc trở về với những thiết bị đơn giản nhưng đầy đủ chức năng là một nhu cầu ngày càng phổ biến. Masstel Fami 60S 4G chính là câu trả lời hoàn hảo cho những ai tìm kiếm sự tiện lợi và dễ sử dụng trong nghe gọi và nhắn tin, đặc biệt với phụ huynh và người lớn tuổi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("55688e12-587e-4a2e-bd65-4c7982ad4f01"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Thiết kế nhỏ gọn, tiện lợi", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("1bf4f7bd-41fa-4c44-8cd8-09ef1611fea0"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Masstel Fami 60S 4G có thiết kế nhỏ gọn và kiểu dáng thân thiện với người dùng. Chiều lòng người dùng bằng chức năng thay pin rời dễ dàng, sản phẩm giải quyết tình trạng pin chai mà không cần phải đến trung tâm bảo hành. Bạn hoàn toàn có thể tự mình thay một viên pin mới một cách thuận tiện khi cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("4529d6a2-e606-4c44-9f17-a1357c788be3"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Bàn phím T9 là điểm nhấn của Masstel Fami 60S 4G, với những phím bấm to và rõ ràng, phù hợp với người lớn tuổi và những ai không quen với bàn phím cảm ứng. Màn hình IPS LCD không chỉ tăng cường trải nghiệm từng hình ảnh mà còn giúp bạn dễ dàng đọc được thông tin dưới mọi điều kiện ánh sáng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("9da159e1-d468-43b3-9626-96dccc1f4fde"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Pin \"trâu\" dùng nhiều ngày", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("9bd2cd6a-782f-4d65-94a0-d28db713f32b"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Với viên pin 2300 mAh, Masstel Fami 60S 4G cho thời gian sử dụng lâu dài ngày, hạn chế nỗi lo cạn pin giữa ngày. Việc tích hợp cổng sạc Type-C thể hiện sự hiện đại và sự tiện lợi khi sạc, một điểm cộng lớn cho một điện thoại phổ thông.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("04a240c5-f1bd-45eb-a4de-04f5c8ce0cc6"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Công nghệ nghe gọi tối ưu", Type = ParseGadgetDescriptionType("BoldText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("b7855564-fe10-46f7-9f30-4e052b954eb7"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Chất lượng cuộc gọi là yếu tố then chốt của Fami 60S 4G, với khả năng hỗ trợ 2 nano SIM và công nghệ 4G VoLTE, từ đó nâng cao đáng kể chất lượng cuộc gọi. Ngoài ra, khả năng lưu trữ lên đến 2000 số điện thoại giúp bạn quản lý danh bạ dễ dàng và chắc chắn không bỏ sót bất kỳ liên lạc quan trọng nào.", Type = ParseGadgetDescriptionType("NormalText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("5d9f816f-36be-4ad0-b769-5db3e1568987"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Nhiều tiện ích đi kèm", Type = ParseGadgetDescriptionType("BoldText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("f44d596a-4d93-4642-bdb7-6e44fc17ef5a"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Chiếc điện thoại Masstel này không chỉ dừng lại ở một chiếc điện thoại nghe gọi. Nó còn mang đến cho bạn radio FM có thể sử dụng mà không cần tai nghe - một phương tiện giải trí tuyệt vời.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("35cce36c-1965-4be5-a40f-846553d6c8bf"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Ngoài ra, máy vẫn hỗ trợ nghe nhạc MP3, cho dù bạn đang làm việc nhà, đi bộ ngoài công viên hay chỉ là muốn thư giãn sau một ngày làm việc mệt mỏi. Đèn pin tích hợp 2 bóng, cung cấp ánh sáng mạnh mẽ, giúp bạn chiếu sáng khi cần thiết.", Type = ParseGadgetDescriptionType("NormalText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("04b40459-facb-4d1a-863b-c6eedf5c224f"), GadgetId = Guid.Parse("6949bc0c-624e-462a-99aa-39eecfb4b0b6"), Value = "Masstel Fami 60S 4G hứa hẹn không chỉ là một điện thoại, mà là một người bạn đồng hành đáng tin cậy trong cuộc sống hằng ngày của bạn. Với sự cân nhắc tỉ mỉ về thiết kế, đây chắc chắn là lựa chọn phù hợp cho người lớn tuổi hay những người muốn sử dụng như là chiếc máy phụ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("8512f296-a9b3-407a-864b-1fd7e25d6fa4"), GadgetId = Guid.Parse("6198835f-ab39-4152-a0b8-ec232d1346d4"), Value = "Mobell chính thức trình làng điện thoại phổ thông Mobell M239, mang ưu điểm với một thiết kế gọn gàng và màu sắc trẻ trung.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
