new GadgetDescription { Id = Guid.Parse("05c8ba68-a68a-4d29-9da7-f68c5ce30f64"), GadgetId = Guid.Parse("6198835f-ab39-4152-a0b8-ec232d1346d4"), Value = "Mặt trước của Mobell M239 là màn hình kích thước 1.77 inch có độ phân giải 128 x 160 Pixels cho hình ảnh rõ ràng, các biểu tượng được hiển thị dễ nhìn và cho thao tác thuận tiện. Mobell M239 được trang bị bàn phím có độ nảy cao, các phím bấm mềm mại và rất dễ thao tác giúp tốc độ ấn nhanh và chính xác hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
