new GadgetDescription { Id = Guid.Parse("13523cad-0614-40ec-b742-7d4235286d1c"), GadgetId = Guid.Parse("6198835f-ab39-4152-a0b8-ec232d1346d4"), Value = "Lưu trữ tốt hơn với bộ nhớ 128 MB", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("5ce37f61-9858-43b8-bde9-be3c80761c16"), GadgetId = Guid.Parse("6198835f-ab39-4152-a0b8-ec232d1346d4"), Value = "Hiệu năng của máy được cung cấp là bộ vi xử lý Unisoc UMS9117-L, RAM 48 MB và bộ nhớ trong 128 MB. Bạn có thể mở rộng dung lượng thông qua thẻ nhớ MicroSD tối đa 64 GB. Ngoài ra, Mobell cũng hỗ trợ cho máy có thể trang bị 2 sim, nhờ đó việc liên lạc trở nên dễ dàng, thuận tiện và có thể luân phiên giữa hai số liên lạc. Rất phù hợp với những người làm nhiều công việc và cần sử dụng các số liên hệ khác nhau.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("f1774734-53ba-42ae-8051-81b342410449"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Bên cạnh sự chạy đua về công nghệ trên những mẫu smartphone, thì đâu đó trên thị trường vẫn còn sót lại một vài hãng còn tiếp tục cải tiến và sản xuất những mẫu điện thoại phổ thông với mục tiêu đáp ứng nhu cầu sử dụng cơ bản. Nổi trội trong đó có thể kể đến chiếc Mobell F209 với màn hình to rõ, viên pin lớn cùng công nghệ mạng di động 4G VoLTE.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("1355b7f5-fc31-4846-845e-33e9d16fa224"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Dễ dàng bỏ túi với thân hình thon gọn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b8ffb509-cedf-48fb-adbb-64be22e3af8a"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Mobell F209 sở hữu một ngoại hình trông khá cổ điển với kiểu dáng bo tròn ở các góc và cạnh viền, cả mặt lưng và bộ khung đều được làm từ nhựa nên máy mang đến cảm giác sử dụng tương đối nhẹ nhàng với khối lượng chỉ 131.5 g.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("608d9733-aa81-429c-ba55-b90ee3b35e83"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/1734f461-33be-4ed7-b73a-e2a99d8a6491.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("e688de81-4603-4b47-a87b-472953f3ce8a"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Màn hình hiển thị to rõ", Type = ParseGadgetDescriptionType("BoldText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("3518b3b5-b3a9-4415-b5eb-9d772b91f3b3"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Phía trước điện thoại sẽ là màn hình TFT LCD, góc nhìn cũng khá rộng để bạn có thể sử dụng máy ở nhiều hướng khác nhau. Ngoài ra ưu điểm của loại tấm nền này là mang đến nội dung có độ sáng tương đối cao, phù hợp cho những tình huống xem tin nhắn hay số điện thoại khi ở những môi trường có độ sáng cao.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("2fd7eeb0-6040-4a54-8f96-c3e9b00f93c8"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/6500efb7-1bc5-4cb3-ac30-95931383c853.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("2c4c214d-da6a-4cc9-999f-df85f71f2ffc"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Thoải mái đàm thoại trong thời gian dài lâu", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("e58852fc-243d-4659-8768-edfe963a392a"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Bên trong máy sẽ là viên pin có dung lượng 1500 mAh, đây được xem là con số lớn trên một chiếc điện thoại phổ thông bởi máy chủ yếu phục vụ một vài tác vụ chính như nghe gọi, nhắn tin nên năng lượng tiêu hao là không quá đáng kể. Vậy nên người dùng có thể an tâm hơn khi sử dụng máy trong nhiều ngày mà không quá lo lắng đến việc hết pin.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("76ecf20c-a87c-4a09-a659-6215cb67d4bb"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/9af27aac-c442-41aa-9dd0-144bd6ac6db6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("77395bab-2167-4985-ad28-564df411c2e5"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Nghe gọi ổn định và thoải mái lưu trữ với 2000 số", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("7dd99ff3-2d01-4f8b-bb79-bf65542c544b"), GadgetId = Guid.Parse("a6f1162c-6694-4921-8e73-0e8b9fc9c3b3"), Value = "Nhằm mang đến chất lượng cuộc gọi ổn định hơn thì hãng mang đến cho máy công nghệ mạng di động 4G VoLTE, nó giúp thu sóng tốt hơn để hạn chế tình trạng mạng chập chờn, ngắt sóng ở những khu vực ngoại thành có tín hiệu kém, từ đó nâng cao trải nghiệm mỗi khi bạn thực hiện các cuộc gọi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("faf90841-2ff2-490b-b2ac-75a814d791df"), GadgetId = Guid.Parse("285566a2-06dd-4406-807b-934b84a9fc16"), Value = "Chiếc điện thoại Mobell F309 là sự lựa chọn phù hợp cho khách hàng đang tìm kiếm một thiết bị di động nhỏ gọn để liên lạc, hỗ trợ mạng di động 4G VoLTE và có thể sử dụng hai SIM nano giúp cho nhu cầu liên lạc bằng việc nghe gọi/nhắn tin được đáp ứng tốt.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("2f14b1d2-4eb5-499e-82e3-92b2159cef13"), GadgetId = Guid.Parse("285566a2-06dd-4406-807b-934b84a9fc16"), Value = "Thiết kế nhỏ gọn", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
