new GadgetDescription { Id = Guid.Parse("2447a6e7-312c-40ef-9e0c-efd7819ed834"), GadgetId = Guid.Parse("d77ad51c-e9da-4457-b4bd-2f7b981cfe24"), Value = "Nghe gọi ổn định cho một ngày dài", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("87025ba1-b103-4c71-b33c-d74e37442131"), GadgetId = Guid.Parse("d77ad51c-e9da-4457-b4bd-2f7b981cfe24"), Value = "Mobell M331 trang bị hai khe nano SIM bên trong máy, cho phép bạn sử dụng hai số điện thoại cùng một lúc. Điều này giúp bạn dễ dàng phân loại và quản lý danh bạ cho công việc và giao tiếp cá nhân. Điểm đáng chú ý khác của Mobell là khả năng hỗ trợ mạng di động 4G VoLTE, cung cấp chất lượng cuộc gọi tốt hơn nhờ thu sóng mạnh mẽ.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("c4faf4b7-e3a3-4743-9be3-6e82e6bddcdf"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Itel it8010 là sự lựa chọn lý tưởng cho những ai đang tìm kiếm một chiếc điện thoại phổ thông với thiết kế nhỏ gọn, dễ sử dụng và đầy đủ các tính năng cần thiết cho cuộc sống hằng ngày.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("082d196c-04cb-4cc5-9fbe-4a3089cf3cf8"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Thiết kế gọn nhẹ, thân thiện", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b4e81d2d-fa42-432e-b93a-e1350f45c13e"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Itel it8010 sở hữu thiết kế dạng rời, mang đến sự tiện lợi khi thay pin. Với khối lượng chỉ 82.5g, chiếc điện thoại này mang lại cảm giác cầm nắm nhẹ nhàng và thoải mái trong lòng bàn tay. Vỏ ngoài được làm từ nhựa chất lượng cao, đảm bảo độ bền và khả năng chống va đập tốt. Điện thoại có ba màu sắc tùy chọn là xanh dương, xanh đen và xanh lá, giúp người dùng dễ dàng lựa chọn theo sở thích cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("2cbe6e5e-9f38-467e-ac51-158850ec4350"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Bàn phím nổi, thao tác dễ dàng", Type = ParseGadgetDescriptionType("BoldText"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("46f1ddef-07fa-4621-9e37-6abbd7bb06a8"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Màn hình rộng 2 inch của Itel it8010 không chỉ đủ lớn để thực hiện các thao tác cơ bản mà còn hiển thị hình ảnh với độ rõ nét tương đối tốt trong tầm giá. Bàn phím T9 truyền thống được thiết kế với các phím nổi, giúp người dùng dễ dàng bấm số mà không lo nhầm lẫn. Đặc biệt, bàn phím còn phát ra âm thanh theo phím khi bấm, hỗ trợ người cao tuổi sử dụng dễ dàng hơn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("d352312e-41f9-42dc-8c8e-50d08d3dbf29"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Pin trâu, sử dụng lâu dài", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
new GadgetDescription { Id = Guid.Parse("91379eb4-61b3-4fda-9dc8-81e971d7ebe9"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Chiếc điện thoại Itel này được trang bị viên pin dung lượng 1000 mAh, đủ để sử dụng trong nhiều ngày liên tục mà không cần phải sạc lại. Thiết kế pin rời cho phép người dùng dễ dàng thay thế khi cần thiết. Điện thoại sử dụng cổng sạc Micro USB, phổ biến và dễ dàng tìm thấy trong mọi gia đình.", Type = ParseGadgetDescriptionType("NormalText"), Index = 6 },
new GadgetDescription { Id = Guid.Parse("69628bda-6148-4dce-b657-30e66b26185b"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Tiện ích đa dạng", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("bf3b12d5-08eb-4c63-a4ff-845becf9dfb9"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Itel it8010 hỗ trợ 2 khe Nano SIM, giúp bạn dễ dàng phân chia giữa công việc và cuộc sống cá nhân. Ngoài ra, điện thoại còn tích hợp các tiện ích như đèn pin, nghe nhạc, xem video và radio, giúp bạn thư giãn sau những giờ làm việc căng thẳng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("3952a96a-a1fb-463a-b35b-39376ddd2420"), GadgetId = Guid.Parse("406088c0-53b9-4c8a-9a25-5e1c7de02862"), Value = "Itel it8010 là chiếc điện thoại phổ thông với đầy đủ các tính năng cần thiết, thiết kế nhỏ gọn và thân thiện, phù hợp với mọi đối tượng người dùng, từ người trẻ cho đến người lớn tuổi.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("a95c2faa-7c3d-4731-b648-d4f3d33e0978"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "Itel it9211 là chiếc điện thoại phổ thông mang đến sự tiện lợi và bền bỉ. Với thiết kế pin rời, màn hình vừa đủ và hỗ trợ kết nối 4G, sản phẩm này đáp ứng nhu cầu cơ bản của người dùng, đặc biệt là những ai chưa quen với smartphone.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("bf08c872-03f0-43e0-a498-cb9dff974a3a"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "Thiết kế và chất liệu nhựa cao cấp", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("0cde9d5f-6090-4ffa-932d-82947aa16669"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "Mặt sau của máy đơn giản với logo Itel ở phía dưới và camera 0.08 MP nằm phía trên. Camera này chỉ hỗ trợ chụp ảnh cơ bản, phù hợp với những ai không yêu cầu chất lượng ảnh cao. Điểm nổi bật ở cạnh trên được tích hợp đèn pin, giúp máy trở thành công cụ chiếu sáng tiện lợi trong những tình huống cần thiết. Jack tai nghe 3.5 mm cùng cổng sạc Type-C hiện đại giúp tăng cường tính kết nối và thuận tiện cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("d67e0b4a-80a6-4b41-9ced-da68eeca00cd"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/8ede262f-194e-4740-85dc-617019806911.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
