new GadgetDescription { Id = Guid.Parse("22b2ee9c-0f5e-42d8-80cd-59289b6ea0f5"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "Màn hình 2.4 inch của Itel it9211 đáp ứng tốt các nhu cầu cơ bản như đọc tin nhắn, xem danh bạ và các tác vụ thường nhật. Dù không quá lớn, nhưng với kích thước này, màu sắc và chất lượng hiển thị vẫn đủ rõ ràng, dễ nhìn trong điều kiện ánh sáng thường.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
