new GadgetDescription { Id = Guid.Parse("0d346c6f-b563-4cfe-81fe-5ef34c4c8636"), GadgetId = Guid.Parse("237a983e-9a65-41be-a4e9-7f9eb4cccb35"), Value = "Viên pin 1000 mAh của Itel it9211 đảm bảo thời gian sử dụng lâu dài, đặc biệt phù hợp cho những người cần một chiếc điện thoại đáng tin cậy mà không phải sạc thường xuyên. Với nhu cầu cơ bản như nghe gọi, nhắn tin, chiếc điện thoại này có thể hoạt động trong nhiều ngày liền trước khi cần sạc lại, tạo sự tiện lợi cho người dùng.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("89bf5ef8-9823-4d32-8356-0a63daa66134"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Itel it2600 một người bạn đồng hành đáng tin cậy trong cuộc sống hằng ngày. Với kiểu dáng nhỏ gọn và vừa vặn lòng bàn tay, chiếc điện thoại mang đến cảm giác thoải mái khi cầm nắm. Bàn phím T9 quen thuộc giúp bạn dễ dàng soạn thảo tin nhắn, bấm gọi.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("3d691122-95e2-482b-a415-98d9367f3da8"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Màn hình rõ ràng, thiết kế chắc chắn và tiện lợi", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("ee203e7a-cd11-4a5d-882b-0bd26f8a26db"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Màn hình 2 inch của Itel it2600 cung cấp độ phân giải QVGA, giúp không gian hiển thị rõ ràng và trực quan. Dù không có công nghệ hiện đại, nhưng màn hình vẫn đảm bảo biểu tượng và văn bản dễ nhìn, giúp người dùng thao tác thuận tiện.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
