new GadgetDescription { Id = Guid.Parse("1b9312c6-00b2-487c-8675-4772a6440b7a"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Bàn phím vật lý của điện thoại được thiết kế với các phím bấm lớn, khoảng cách giữa các phím hợp lý, giúp người dùng dễ dàng thao tác. Các phím được làm từ chất liệu bền bỉ cho độ nảy tốt, mang đến cảm giác gõ phím êm ái và chính xác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 5 },
