new GadgetDescription { Id = Guid.Parse("6655b57f-2015-4948-872c-6c8ba25a97b3"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Thời lượng pin ấn tượng và cổng sạc Type-C hiện đại", Type = ParseGadgetDescriptionType("BoldText"), Index = 7 },
new GadgetDescription { Id = Guid.Parse("1f66b97a-c712-4f7b-8903-9fb09c38e1de"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Viên pin 1000 mAh trên mẫu điện thoại phổ thông này mang đến thời lượng sử dụng đáng kinh ngạc. Bạn có thể yên tâm rằng chiếc điện thoại này sẽ đồng hành cùng bạn trong suốt nhiều dài. Bên cạnh đó, cổng sạc Type-C hiện đại không chỉ là một nâng cấp về công nghệ mà còn là một sự lựa chọn thông minh, giúp bạn tiết kiệm thời gian và công sức.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
