new GadgetDescription { Id = Guid.Parse("48865203-30b0-4457-a8cf-8e00a4024fa1"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Kết nối 4G, đàm thoại chất lượng cao", Type = ParseGadgetDescriptionType("BoldText"), Index = 10 },
new GadgetDescription { Id = Guid.Parse("03b6b775-5128-4526-9d30-22b4c1f93103"), GadgetId = Guid.Parse("9104a7f4-dc46-4e7a-be0b-9be10a69ee58"), Value = "Itel it2600 hỗ trợ 2 SIM, mang đến sự linh hoạt cho người dùng khi có thể sử dụng hai số điện thoại cùng lúc, phù hợp cho việc tách biệt công việc và cá nhân. Ngoài ra, với khả năng kết nối 4G, chiếc điện thoại này đảm bảo chất lượng đàm thoại ổn định, âm thanh rõ ràng, giảm thiểu nhiễu và gián đoạn.", Type = ParseGadgetDescriptionType("NormalText"), Index = 11 },
new GadgetDescription { Id = Guid.Parse("ca52881f-fb74-4a83-9a5a-81d1c6bff8f5"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Benco 4G G3 là điện thoại phổ thông gọn nhẹ, dễ dùng. Máy có màn hình 1.77 inch, hỗ trợ 2 Nano SIM, kết nối 4G, bàn phím T9, đèn pin và pin 1000 mAh cho thời gian dùng lâu. Ngoài ra, máy còn hỗ trợ nghe FM, nhạc và xem video qua thẻ nhớ, đáp ứng nhu cầu cơ bản.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("4dca336b-f706-435b-a2fb-fdceec21b8f6"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Vẻ ngoài nhỏ gọn, cầm nắm thoải mái", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("70b02537-6f67-4cbf-acad-f1a8a34b18af"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Thiết kế tổng quan của máy khá đơn giản, quen thuộc nhưng tinh tế, với mặt lưng trơn nhẵn và logo được đặt tinh tế ở góc dưới, tạo nên vẻ ngoài thanh lịch nhưng không kém phần bền bỉ. Phần loa của máy được bố trí ở phía trên mặt lưng, đảm bảo âm thanh rõ ràng và dễ nghe.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
