new GadgetDescription { Id = Guid.Parse("8a399ce7-933d-47dc-aca3-874692a78b68"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Bàn phím của Benco 4G G3 được thiết kế theo dạng T9 truyền thống, mang lại cảm giác quen thuộc cho những ai đã từng sử dụng các dòng điện thoại phím trước đây. Độ nảy của phím cao, đảm bảo mỗi lần bấm đều chính xác. Khoảng cách giữa các phím được bố trí hợp lý, rộng vừa đủ để tránh bấm nhầm phím khi thao tác.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
new GadgetDescription { Id = Guid.Parse("4f219d5a-c66a-4058-adc4-b625de754bc0"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Màn hình cho chất lượng hiển thị vừa đủ", Type = ParseGadgetDescriptionType("BoldText"), Index = 5 },
