new GadgetDescription { Id = Guid.Parse("f6aa4384-fd08-44f2-9272-50b5bbf6b21f"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Benco 4G G3 không chỉ là một chiếc điện thoại để gọi điện và nhắn tin, mà còn mang đến nhiều tiện ích hữu dụng khác. Máy tích hợp đèn pin mạnh mẽ, hỗ trợ nghe đài FM, nghe nhạc và xem video được lưu trữ trên thẻ nhớ, giúp người dùng có những trải nghiệm giải trí cơ bản mà không cần đến các thiết bị phức tạp.", Type = ParseGadgetDescriptionType("NormalText"), Index = 8 },
new GadgetDescription { Id = Guid.Parse("cc7ac473-c5f0-4efc-9f90-30eddbfb463d"), GadgetId = Guid.Parse("5aeefeca-a5f4-483f-82cd-176234ab6f58"), Value = "Tóm lại, Benco 4G G3 là một chiếc điện thoại phổ thông với thiết kế đơn giản, dễ sử dụng, nhiều tiện ích thực tế và pin bền bỉ, rất phù hợp cho những ai tìm kiếm một chiếc điện thoại cơ bản nhưng vẫn đầy đủ chức năng cần thiết trong cuộc sống hằng ngày.", Type = ParseGadgetDescriptionType("NormalText"), Index = 9 },
new GadgetDescription { Id = Guid.Parse("f6833f94-a8c9-44ca-9b9c-83b58164586b"), GadgetId = Guid.Parse("96903d3b-b52f-4be7-8a7e-dadd26dfbffb"), Value = "Trong thế giới âm thanh di động hiện đại, loa Bluetooth JBL Clip 5 nổi bật như một lựa chọn lý tưởng cho những người yêu thích sự tiện lợi và chất lượng âm thanh. Với thiết kế nhỏ gọn, nhẹ nhàng và tính năng chống nước IP67, JBL Clip 5 sẽ là người bạn đồng hành hoàn hảo cho những giai điệu yêu thích của bạn.", Type = ParseGadgetDescriptionType("BoldText"), Index = 0 },
new GadgetDescription { Id = Guid.Parse("906f7639-1f7b-4c30-a4fe-7852dcabe720"), GadgetId = Guid.Parse("96903d3b-b52f-4be7-8a7e-dadd26dfbffb"), Value = "Kích thước nhỏ gọn tiện lợi khi mang bất cứ đâu", Type = ParseGadgetDescriptionType("BoldText"), Index = 1 },
new GadgetDescription { Id = Guid.Parse("b11b9edd-a584-4c0e-ac60-06c2f3990d57"), GadgetId = Guid.Parse("96903d3b-b52f-4be7-8a7e-dadd26dfbffb"), Value = "Loa Bluetooth sở hữu vẻ ngoài nhỏ gọn giúp bạn dễ dàng cầm nắm và mang theo bên mình. Khối lượng nhẹ chỉ 0.292 kg cũng khiến loa trở thành một lựa chọn lý tưởng cho những chuyến đi, hoạt động ngoài trời hay đơn giản là di chuyển trong nhà.", Type = ParseGadgetDescriptionType("NormalText"), Index = 2 },
new GadgetDescription { Id = Guid.Parse("c158cdb4-c740-4ba3-ab47-8ffd63ad4a32"), GadgetId = Guid.Parse("96903d3b-b52f-4be7-8a7e-dadd26dfbffb"), Value = "https://storage.googleapis.com/fbdemo-f9d5f.appspot.com/Gadgets/57d59216-c0a6-4113-95a0-b0bc12f299e6.jpg", Type = ParseGadgetDescriptionType("Image"), Index = 3 },
new GadgetDescription { Id = Guid.Parse("1ff0238f-f000-4cc6-a45b-ee31fb64ead9"), GadgetId = Guid.Parse("96903d3b-b52f-4be7-8a7e-dadd26dfbffb"), Value = "Thiết kế hình tròn độc đáo không chỉ tạo nên vẻ ngoài thu hút mà còn tiện lợi khi treo lên balo, xe đạp hoặc các vật dụng khác nhờ vào móc treo tích hợp. Ngoài ra JBL Clip 5 có nhiều màu sắc nổi bật, từ đen cổ điển đến đỏ rực rỡ, cho phép bạn lựa chọn theo sở thích cá nhân.", Type = ParseGadgetDescriptionType("NormalText"), Index = 4 },
